mapper.select:
select

from SECURITYCLUE


         SCID = #{scid,jdbcType=VARCHAR}


         and SCGOODS = #{scgoods,jdbcType=VARCHAR}


         and SCCOMPANY =  #{sccompany,jdbcType=VARCHAR}


         and  SCPEOPLE = #{scpeople,jdbcType=VARCHAR}


         and SCMP = #{scmp,jdbcType=VARCHAR}


         and SCADDRESS = #{scaddress,jdbcType=VARCHAR}


         and SCSKETCH = #{scsketch,jdbcType=VARCHAR}


         and SCCREATETIME = #{sccreatetime,jdbcType=DECIMAL}


         and SCPROSTATUS = #{scprocstatus,jdbcType=DECIMAL}
